import React, { useState, useEffect } from 'react';

const ShippingProgressBar = () => {
  const [statuses, setStatuses] = useState([]);
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    // Fetch data from the mock API (dummy-api.json)
    fetch('/dummy-api.json')
      .then((response) => response.json())
      .then((data) => {
        setStatuses(data.statuses);
        const completedStatusCount = data.statuses.filter((status) => status.status === 'Delivered').length;
        const totalStatusCount = data.statuses.length;
        const progressPercentage = (completedStatusCount / totalStatusCount) * 100;
        setProgress(progressPercentage);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <div className="container mx-auto my-4">
      <h2 className="text-2xl font-semibold mb-4">Shipping Progress</h2>
      <div className="w-16 h-64 bg-gray-200 relative">
        <div
          className="h-full bg-green-500 absolute bottom-0"
          style={{ height: `${progress}%` }}
        ></div>
      </div>
      <p className="mt-2 text-gray-700 font-semibold">
        {progress.toFixed(2)}% Complete
      </p>
      <div className="mt-4">
        <h3 className="text-lg font-semibold mb-2">Status Timeline</h3>
        <ul>
          {statuses.map((status, index) => (
            <li key={index} className="mb-2">
              <span className="mr-2 text-gray-500">{status.date}:</span>
              <span className="font-semibold">{status.status}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default ShippingProgressBar;
