import React, { useState, useEffect } from 'react';
import './ProgressBar.css';

const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: 'DISPATCHED' });

  useEffect(() => {
    // Simulate fetching data from a dummy API
    setTimeout(() => {
      const randomStatus = Math.random() < 0.5 ? 'DISPATCHED' : 'OUTFORDEL';

      setApiData({ status: randomStatus });
    }, 2000); // Simulate API request every 2 seconds
  }, []);

  const labels = ['DISPATCHED', 'OUTFORDEL'];

  return (
    <div className="progress-bar">
      <div className="progress-labels">
        {labels.map((label) => (
          <div
            key={label}
            className={`progress-label ${apiData.status === label ? 'active' : ''}`}
          >
            {label}
          </div>
        ))}
      </div>
      <div className={`progress-bar ${apiData.status}`} />
    </div>
  );
};

export default ProgressBar;
