import React, { useState, useEffect } from 'react';
import './ProgressBar.css';

const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: '' });

  useEffect(() => {
    // Replace 'your-api-url-here' with the actual URL of your API endpoint
    fetch('your-api-url-here')
      .then((response) => response.json())
      .then((data) => {
        // Assuming your API response contains a 'status' field
        setApiData({ status: data.status });
      })
      .catch((error) => {
        console.error('Error fetching data from API:', error);
      });
  }, []);

  const labels = ['DISPATCHED', 'REACHED'];

  return (
    <div className="progress-bar">
      <div className="progress-labels">
        {labels.map((label) => (
          <div
            key={label}
            className={`progress-label ${apiData.status === 'REACHED' && label === 'REACHED' ? 'active' : ''}`}
          >
            {label}
          </div>
        ))}
      </div>
      <div className={`progress-bar ${apiData.status}`} />
    </div>
  );
};

export default ProgressBar;
