import React, { useState, useEffect } from 'react';
import './ProgressBar.css'
const ProgressBar = () => {
  const [apiData, setApiData] = useState({ status: 'Dispatched' });

  useEffect(() => {
    // Simulate fetching data from a dummy API
    setTimeout(() => {
      const randomStatus = Math.random() < 0.25 ? 'Dispatched' :
                           Math.random() < 0.5 ? 'Reached Hub1' :
                           Math.random() < 0.75 ? 'Reached Hub2' :
                           'Out for Delivery';

      setApiData({ status: randomStatus });
    }, 2000); // Simulate API request every 2 seconds
  }, []);

  return (
    <div className="progress-bar">
      <div className="progress-labels">
        <div className={`progress-label ${apiData.status === 'Dispatched' ? 'active' : ''}`}>Dispatched</div>
        <div className={`progress-label ${apiData.status === 'Reached Hub1' ? 'active' : ''}`}>Reached Hub1</div>
        <div className={`progress-label ${apiData.status === 'Reached Hub2' ? 'active' : ''}`}>Reached Hub2</div>
        <div className={`progress-label ${apiData.status === 'Out for Delivery' ? 'active' : ''}`}>Out for Delivery</div>
      </div>
      <div className="progress-bar-container">
        <div className={`progress ${apiData.status}`} />
      </div>
    </div>
  );
};

export default ProgressBar;
